import React from "react";
import { useState } from "react";
import ContactFields from "./ContactFields";

const Contact = () => {
  // useState for form handling `value:actualValue`
  const [data, setData] = useState({
    fullname: "",
    phone: "",
    email: "",
    message: "",
  });

  // changing the state of default `value:actualValue` pairs
  const changeData = (e) => {
    // object destructuring
    const { name, value } = e.target;
    setData((prevData) => {
      return {
        ...prevData,
        [name]: value,
      };
    });
  };

  const submitData = (e) => {
    e.preventDefault();
    alert(
      `My name is ${data.fullname}. My mobile number is ${data.phone} and email is ${data.email}, I want to say ... ${data.message}`
    );
  }

  return (
    <>
      <div className="my-5">
        <h1 className="text-center">Contact Us</h1>
      </div>
      <div className="container contact_div">
        <div className="row">
          <div className="col-md-6 mx-auto">
            <form onSubmit={submitData}>
              {ContactFields.map((value) => {
                return (
                  <div className="mb-3">
                    <label htmlFor={value.labelfor} className="form-label">
                      {value.labelname}
                    </label>
                    {value.labelname === "Message" ? (
                      <textarea
                        className="form-control"
                        id={value.labelfor}
                        name="message"
                        // accessing the data object values
                        value={data[value.inputName]}
                        onChange={changeData}
                        rows="3"
                      ></textarea>
                    ) : (
                      <input
                        type={value.inputType}
                        className="form-control"
                        name={value.inputName}
                        value={data[value.inputName]}
                        onChange={changeData}
                        id={value.labelfor}
                        placeholder={value.placeholder}
                      />
                    )}
                  </div>
                );
              })}
              <div className="col-12 d-flex justify-content-center">
                <button className="btn btn-primary" type="submit">
                  Submit form
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Contact;
